import React, { Component } from "react";
import * as reactbootstrap from "react-bootstrap";

class CreateTask extends Component {
  constructor(props) {
    super(props);
    this.state = {

    };
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidUpdate(prevProps, prevState) {
    // if (prevProps.editId !== this.props.editId) {
    //
    // }
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value,
    });
  }

  render() {
    const { title, description, type, taskTypes, DateTime, added } = this.state;
    return (
      <reactbootstrap.Container className="pt-5 px-0">
        <div style={{ color: "#EC661C", fontSize: "20px" }}>
          <span>
            <h4>{"Add movie"}</h4>
          </span>
          <reactbootstrap.Button
            style={{
              backgroundColor: "#EC661C",
              borderColor: "black",
              marginBottom: "15px"
            }}
            type="button"
            onClick={(e) => this.creattask(e)}
          >
            {"New"}
          </reactbootstrap.Button>
        </div>
        <reactbootstrap.Form style={{ width: "600px", height: "200px" }}>
          <reactbootstrap.FormGroup>
            <div style={{ marginbottom: "15px", border: "0px" }}>
              <label style={{ color: "#EC661C", fontSize: "14px" }}>
                {"Title"}
                <span style={{ color: "red" }}> * </span>
              </label>
              <input
                name="title"
                value={title}
                type="text"
                style={{
                  marginBottom: "15px",
                  width: "250px",
                  borderColor: "black",
                  borderWidth: "3px"
                }}
                placeholder={"Task title"}
                onChange={this.handleChange}
                autoComplete="off"
              />
            </div>
            {added === true && title === "" && (
              <div style={{ color: "red", fontSize: "15px" }}>
                {"Title field is required"}
              </div>
            )}
          </reactbootstrap.FormGroup>
          <reactbootstrap.FormGroup>
            <div style={{ marginbottom: "15px", border: "0px" }}>
              <label style={{ color: "#EC661C", fontSize: "14px" }}>
                {"Description"}
              </label>
              <input
                name="description"
                value={description}
                type="textarea"
                style={{
                  marginBottom: "15px",
                  width: "250px",
                  borderColor: "black",
                  borderWidth: "3px"
                }}
                placeholder={"Description"}
                onChange={this.handleChange}
                autoComplete="off"
              />
            </div>
          </reactbootstrap.FormGroup>
          <reactbootstrap.FormGroup>
            <div style={{ marginbottom: "15px", border: "0px" }}>
              <label style={{ color: "#EC661C", fontSize: "14px" }}>
                {"Type"}
                <span style={{ color: "red" }}> * </span>
              </label>
              <reactbootstrap.FormControl
                as="select"
                name="type"
                className="input_sw"
                value={type}
                onChange={this.handleChange}
              >
                <option>Select type</option>
                {Object.values(taskTypes).map((taskType) => (
                  <option value={taskType.id}>{taskType.name}</option>
                ))}
              </reactbootstrap.FormControl>
            </div>
          </reactbootstrap.FormGroup>
          <reactbootstrap.FormGroup>
            <div style={{ marginbottom: "15px", border: "0px" }}>
              <label style={{ color: "#EC661C", fontSize: "14px" }}>
                {"Due at"}
                <span style={{ color: "red" }}> * </span>
              </label>
              <DateTimePicker
                onChange={(e) => this.handleDateChange(e)}
                format="dd/MM/yyyy hh:mm:ss  a"
                value={this.state.DateTime}
              />
            </div>
          </reactbootstrap.FormGroup>
          <div>
            <reactbootstrap.Button
              style={{
                float: "left",
                backgroundColor: "#EC661C",
                borderColor: "black",
                marginBottom: "25px",
                marginTop: "25px",
                width: "80px"
              }}
              type="button"
              // onClick={(e) => this.addtask(e)}
            >
              {"Add"}
            </reactbootstrap.Button>
          </div>
        </reactbootstrap.Form>
      </reactbootstrap.Container>
    );
  }
}

export default CreateTask;
