import React, { Component } from 'react';
import * as reactbootstrap from 'react-bootstrap';

class ManageMovies extends Component {
  constructor(props) {
    super(props)
    this.state = {
      moviesList: JSON.parse(localStorage.getItem('movies')),
      showAddMovie: false,
    }

    this.handleEdit = this.handleEdit.bind(this)
  }

  handleEdit(id) {
    console.log(id);
    this.setState({
      showAddMovie: true,
    })
  }

  render() {
    const { moviesList } = this.state;

    const createTemplateContent = (
        <reactbootstrap.Modal
            size='xl'
            show={this.state.showAddMovie}
            onHide={this.handleHide}
            dialogClassName="modal-90w emailDetailpopup"
            aria-labelledby="example-custom-modal-styling-title"
        >
          <reactbootstrap.Modal.Header closeButton>
              <reactbootstrap.Modal.Body>
                <div class="col-md-12 mt-3 mb-3 ">
                    <div className="row">
                    <div class="col-md-8">
                        <div className="mb-3" style={{ borderBottom: '1px solid lightgray' }} >
                            <span><h4>{'Add movie'}</h4></span>
                        </div>
                        {/* <div>
                            <CreateEmailTemplate allvalues={props.allvalues} mainAction={props.action} kpiId={props.kpiId} Submitted={props.Submitted} Valid={props.Valid} action={state.templateAction} templateId={state.templateId} language_list={state.language_list} handleNewTemplates={handleNewTemplates}></CreateEmailTemplate>
                        </div> */}
                    </div>
                    <div class="col-md-4 pl-5">
                        {/* <reactbootstrap.Form.Label><h4>{t('Tokens:')}</h4></reactbootstrap.Form.Label>
                        <ul style={{paddingLeft: '17px', fontSize: '14px'}}>
                            {Object.values(window.KPI_TOKENS).map(item => (
                                <li>{item}</li>
                              ))
                            }
                        </ul> */}
                    </div>
                    </div>
                </div>
              </reactbootstrap.Modal.Body>
          </reactbootstrap.Modal.Header>
        </reactbootstrap.Modal>
    );

    return(
      <reactbootstrap.Container className="pt-5 px-0">
        <div style={{ color: '#EC661C', fontSize: '20px'}} >
            <span><h4>{"Manage movies"}</h4></span>
        </div>
        <reactbootstrap.Form style={{ overflow: "auto" }}>
        {/* <reactbootstrap.Form style={{ width: '600px', height: '200px'}}> */}
          <reactbootstrap.Table style={{ display: 'block', border: '2px solid lightgray', 'width' : '100%'}}>
            <thead style={{ backgroundColor: '#EC661C', color: 'white', position: 'sticky', top: '0', textAlign: 'center' }}>
                <tr style={{ textAlign: 'center', border: '2px solid black' }}>
                  <th>{'Movie name'}</th>
                  <th>{'Year'}</th>
                  <th>{'Rate'}</th>
                  <th>{'Duration'}</th>
                  <th>{'Edit'}</th>
                </tr>
              </thead>
              <tbody style={{ backgroundColor: 'gray', color: 'white', position: 'sticky', top: '0', textAlign: 'center' }}>
                {Object.keys(moviesList).length > 0 &&
                  <>
                    {Object.values(moviesList).map((item) => (
                      <tr style={{ textAlign: 'center', border: '2px solid black' }}>
                         <td>{item.name}</td>
                         <td>{item.year}</td>
                         <td>{item.rating}</td>
                         <td>{item.duration}</td>
                         <td>
                           <reactbootstrap.Button style={{ float: 'right',backgroundColor: '#EC661C',borderColor: "black", marginBottom: '25px', marginTop: '25px', width: '80px' }} type="button" onClick={(e) => this.handleEdit(item.id)}>
                                 {'Edit'}
                           </reactbootstrap.Button>
                         </td>
                      </tr>
                    ))}
                  </>
                }
              </tbody>
          </reactbootstrap.Table>
          {/* <div>
            <reactbootstrap.Button style={{ float: 'right',backgroundColor: '#EC661C',borderColor: "black", marginBottom: '25px', marginTop: '25px', width: '80px' }} type="button" onClick={e => this.logOut()}>
                  {'LogOut'}
            </reactbootstrap.Button>
          </div> */}
        </reactbootstrap.Form>
      </reactbootstrap.Container>
    )
  }

}

export default ManageMovies
