import React, { Component } from 'react';
import * as reactbootstrap from 'react-bootstrap';
import AddMovie from './AddMovie'

class ManageMovies extends Component {
  constructor(props) {
    super(props)
    this.state = {
      moviesList: JSON.parse(localStorage.getItem('movies')),
      showAddMovie: false,
    }

    this.handleEdit = this.handleEdit.bind(this)
    this.handleHide = this.handleHide.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
    // this.handleChange = this.handleChange.bind(this);
  }

  handleEdit(id) {
    console.log(id);
    this.setState({
      showAddMovie: true,
    })
  }

  handleHide() {
    this.setState({
      showAddMovie: false,
    })
  }

  // handleChange(e) {
  //   const { name, value } = e.target;
  //   this.setState({
  //     [name]: value,
  //   });
  // }

  handleSubmit() {
    this.setState({
      showAddMovie: false,
    })
  }

  render() {
    const { moviesList } = this.state;

    const createTemplateContent = (
      <Modal.Dialog>
        <Modal.Header closeButton>
          <Modal.Title>Modal title</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>Modal body text goes here.</p>
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary">Close</Button>
          <Button variant="primary">Save changes</Button>
        </Modal.Footer>
      </Modal.Dialog>
    )
    // co

    return(
      <reactbootstrap.Container className="pt-5 px-0">
        <div style={{ color: '#EC661C', fontSize: '20px'}} >
            <span><h4>{"Manage movies"}</h4></span>
        </div>
        <reactbootstrap.Form style={{ overflow: "auto" }}>
        {/* <reactbootstrap.Form style={{ width: '600px', height: '200px', 'textAlign': 'center'}}> */}
          <reactbootstrap.Table style={{ display: 'block', border: '2px solid lightgray', 'width' : '100%'}}>
            <thead style={{ backgroundColor: '#EC661C', color: 'white', position: 'sticky', top: '0', textAlign: 'center' }}>
                <tr style={{ textAlign: 'center', border: '2px solid black' }}>
                  <th>{'Movie name'}</th>
                  <th>{'Year'}</th>
                  <th>{'Rate'}</th>
                  <th>{'Duration'}</th>
                  <th>{'Edit'}</th>
                </tr>
              </thead>
              <tbody style={{ backgroundColor: 'gray', color: 'white', position: 'sticky', top: '0', textAlign: 'center' }}>
                {Object.keys(moviesList).length > 0 &&
                  <>
                    {Object.values(moviesList).map((item) => (
                      <tr style={{ textAlign: 'center', border: '2px solid black' }}>
                         <td>{item.name}</td>
                         <td>{item.year}</td>
                         <td>{item.rating}</td>
                         <td>{item.duration}</td>
                         <td>
                           <reactbootstrap.Button style={{ float: 'right',backgroundColor: '#EC661C',borderColor: "black", marginBottom: '25px', marginTop: '25px', width: '80px' }} type="button" onClick={(e) => this.handleEdit(item.id)}>
                                 {'Edit'}
                           </reactbootstrap.Button>
                         </td>
                      </tr>
                    ))}
                  </>
                }
                {createTemplateContent}
              </tbody>
          </reactbootstrap.Table>
          {/* <div>
            <reactbootstrap.Button style={{ float: 'right',backgroundColor: '#EC661C',borderColor: "black", marginBottom: '25px', marginTop: '25px', width: '80px' }} type="button" onClick={e => this.logOut()}>
                  {'LogOut'}
            </reactbootstrap.Button>
          </div> */}
        </reactbootstrap.Form>
      </reactbootstrap.Container>
    )
  }

}

export default ManageMovies
