import React, { Component } from "react";
import * as reactbootstrap from "react-bootstrap";
import DateTimePicker from "react-datetime-picker";

class CreateTask extends Component {
  constructor(props) {
    super(props);
    this.state = {

    };
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidUpdate(prevProps, prevState) {
    // if (prevProps.editId !== this.props.editId) {
    //
    // }
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value,
    });
  }

  render() {
    const { name, year, rating, duration, } = this.state;
    return (
      <reactbootstrap.Container className="pt-5 px-0">
        <div style={{ color: "#EC661C", fontSize: "20px" }}>
          <span>
            <h4>{"Add movie"}</h4>
          </span>
        </div>
        <reactbootstrap.Form style={{ width: "600px", height: "200px" }}>
          <reactbootstrap.FormGroup>
            <div style={{ marginbottom: "15px", border: "0px" }}>
              <label style={{ color: "#EC661C", fontSize: "14px" }}>
                {"Movie name"}
                <span style={{ color: "red" }}> * </span>
              </label>
              <input
                name="name"
                value={name}
                type="text"
                style={{
                  marginBottom: "15px",
                  width: "250px",
                  borderColor: "black",
                  borderWidth: "3px"
                }}
                placeholder={"Movie name"}
                onChange={this.handleChange}
                autoComplete="off"
              />
            </div>
            {name === "" && (
              <div style={{ color: "red", fontSize: "15px" }}>
                {"Movie name field is required"}
              </div>
            )}
          </reactbootstrap.FormGroup>
          <reactbootstrap.FormGroup>
            <div style={{ marginbottom: "15px", border: "0px" }}>
              <label style={{ color: "#EC661C", fontSize: "14px" }}>
                {"Relesed year"}
                <span style={{ color: "red" }}> * </span>
              </label>
              <DateTimePicker
                onChange={(e) => this.handleDateChange(e)}
                format="dd/MM/yyyy"
                value={year}
              />
            </div>
          </reactbootstrap.FormGroup>
          <reactbootstrap.FormGroup>
            <div style={{ marginbottom: "15px", border: "0px" }}>
              <label style={{ color: "#EC661C", fontSize: "14px" }}>
                {"Rating"}
              </label>
              <input
                name="rating"
                value={rating}
                type="textarea"
                style={{
                  marginBottom: "15px",
                  width: "250px",
                  borderColor: "black",
                  borderWidth: "3px"
                }}
                placeholder={"Rating"}
                onChange={this.handleChange}
                autoComplete="off"
              />
            </div>
          </reactbootstrap.FormGroup>
          <reactbootstrap.FormGroup>
            <div style={{ marginbottom: "15px", border: "0px" }}>
              <label style={{ color: "#EC661C", fontSize: "14px" }}>
                {"Duration"}
              </label>
              <input
                name="duration"
                value={duration}
                type="textarea"
                style={{
                  marginBottom: "15px",
                  width: "250px",
                  borderColor: "black",
                  borderWidth: "3px"
                }}
                placeholder={"Duration"}
                onChange={this.handleChange}
                autoComplete="off"
              />
            </div>
          </reactbootstrap.FormGroup>
          <div>
            <reactbootstrap.Button
              style={{
                float: "left",
                backgroundColor: "#EC661C",
                borderColor: "black",
                marginBottom: "25px",
                marginTop: "25px",
                width: "80px"
              }}
              type="button"
              // onClick={(e) => this.addtask(e)}
            >
              {"Cancel"}
            </reactbootstrap.Button>
          </div>
          <div>
            <reactbootstrap.Button
              style={{
                float: "right",
                backgroundColor: "#EC661C",
                borderColor: "black",
                marginBottom: "25px",
                marginTop: "25px",
                width: "80px"
              }}
              type="button"
              // onClick={(e) => this.addtask(e)}
            >
              {"Add"}
            </reactbootstrap.Button>
          </div>
        </reactbootstrap.Form>
      </reactbootstrap.Container>
    );
  }
}

export default CreateTask;
