import React, { Component } from 'react';
import * as reactbootstrap from 'react-bootstrap';
import Modal from "react-bootstrap/Modal";
// import Popup from 'reactjs-popup';
import AddMovie from './AddMovie';
import MovieDetails from './MovieDetails';
import { SortableContainer, SortableElement } from 'react-sortable-hoc';

class ManageMovies extends Component {
  constructor(props) {
    super(props)
    this.state = {
      moviesList: localStorage.getItem('movies') ? JSON.parse(localStorage.getItem('movies')) : {} ,
      showAddMovie: false,
      showMovie: false,
    }

    this.handleView = this.handleView.bind(this)
    this.handleHide = this.handleHide.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  componentDidUpdate(prevProps, prevState) {

    let movies = localStorage.getItem('movies') ? JSON.parse(localStorage.getItem('movies')) : {};
    if (localStorage.getItem('newMovie') == 1) {
      this.setState({
        moviesList: movies,
      },(localStorage.setItem('newMovie', 0)))
    }
  }

  handleView(id) {
    // this.setState({
    //   showMovie: true,
    //   movieId: id,
    // })
    window.location.href = '/moviedetails/' + id;
  }

  handleHide() {
    this.setState({
      showAddMovie: false,
    })
  }

  handleSubmit(e) {
    this.setState({
      showAddMovie: false,
    })
  }


  searchData(e) {
    let list = []
    let res = ''
    let allMovies =  localStorage.getItem('movies') ?  JSON.parse(localStorage.getItem('movies')): {}
    list = Object.values(allMovies).filter(function (item) {
      res = item.name.toLowerCase().search(e.target.value.toLowerCase()) !== -1;
      return res;
    });
    this.setState({
      moviesList: list,
    })
  }

  render() {
    const { moviesList } = this.state;

    // const createTemplateContent = (
    //   <Modal show={this.state.showAddMovie} onHide={this.handleHide}>
    //   <Modal.Header>Hi</Modal.Header>
    //   <Modal.Body>asdfasdf</Modal.Body>
    //   <Modal.Footer>This is the footer</Modal.Footer>
    //   </Modal>
      // <Popup trigger={<button> Trigger</button>} position="right center">
      // <div>Popup content here !!</div>
      // </Popup>
    // )
    const createTemplateContent = (
        <reactbootstrap.Modal
            size='lg'
            show={this.state.showAddMovie}
            onHide={this.handleHide}
            style={{ 'display': 'flex' }}
            dialogClassName="modal-90w emailDetailpopup"
            // aria-labelledby="example-custom-modal-styling-title"
        >
          <reactbootstrap.Modal.Header>
            <reactbootstrap.Modal.Title>
              {/* <reactbootstrap.Modal.Title id="contained-modal-title-vcenter"> */}
                  {/* Add movie */}
          </reactbootstrap.Modal.Title>
              <reactbootstrap.Modal.Body className="px-0">
                <AddMovie handleSubmit={this.handleSubmit} movieId={this.state.movieId}/>
              </reactbootstrap.Modal.Body>
              {/* <reactbootstrap.Button style={{ float: 'left',backgroundColor: '#EC661C',borderColor: "black", marginBottom: '25px', marginTop: '25px', width: '80px' }} type="button" onClick={(e) => this.handleHide()}>
                    {'Cancel'}
              </reactbootstrap.Button>
              <reactbootstrap.Button style={{ float: 'right',backgroundColor: '#EC661C',borderColor: "black", marginBottom: '25px', marginTop: '25px', width: '80px' }} type="button">
                    {'Create'}
              </reactbootstrap.Button> */}
          </reactbootstrap.Modal.Header>
        </reactbootstrap.Modal>
    );

    return(
      <reactbootstrap.Container className="pt-5 px-0">
        <div style={{ color: '#EC661C', fontSize: '20px'}} >
          <reactbootstrap.Button style={{ float: 'right',backgroundColor: '#EC661C',borderColor: "black", marginBottom: '25px', marginTop: '25px', width: '80px' }} type="button" onClick={(e) => this.setState({showAddMovie: true, movieId : null})}>
              {'Add movie'}
          </reactbootstrap.Button>
        </div>
        <div style={{ color: '#EC661C', fontSize: '20px'}} >
            <span><h4>{"Manage movies"}</h4></span>
        </div>
        <reactbootstrap.Form>
        {/* <reactbootstrap.Form style={{ width: '600px', height: '200px', 'textAlign': 'center' , style={{ overflow: "auto" }}}}> */}
        {/* <reactbootstrap.Table> */}
        {/* <input type="text" placeholder={"What are you looking for ?"} autoFocus onChange={(e) => this.searchData(e)} /><br /> */}
          <input type="text" style={{ borderRadius: "5px", borderColor: "#EC661C", height: '20px', width: "100%" }} placeholder={"What are you looking for ?"} autoFocus onChange={(e) => this.searchData(e)} /><br />
          <reactbootstrap.Table style={{ border: '2px solid lightgray', 'width' : '80%', 'marginLeft': '60px', 'marginRight': '60px' }}>
            <thead style={{ backgroundColor: '#EC661C', color: 'white', position: 'sticky', top: '0', textAlign: 'center' }}>
                <tr style={{ textAlign: 'center', border: '2px solid black' }}>
                  <th>{'Movie name'}</th>
                  <th>{'Year'}</th>
                  <th>{'Rate'}</th>
                  <th>{'Duration'}</th>
                  <th>{'View'}</th>
                </tr>
              </thead>
              <tbody style={{ backgroundColor: 'gray', color: 'white', position: 'sticky', top: '0', textAlign: 'center' }}>
                {Object.keys(moviesList).length > 0 &&
                  <>
                    {Object.values(moviesList).map((item) => (
                      <tr style={{ textAlign: 'center', border: '2px solid black' }}>
                         <td>{item.name}</td>
                         <td>{(new Date(item.year)).getFullYear()}</td>
                         <td>{item.rating}</td>
                         <td>{item.duration}</td>
                         <td>
                           <reactbootstrap.Button style={{ backgroundColor: '#EC661C',borderColor: "black", marginBottom: '25px', marginTop: '25px', width: '80px' }} type="button" onClick={(e) => this.handleView(item.id)}>
                                 {'View'}
                           </reactbootstrap.Button>
                         </td>
                      </tr>
                    ))}
                  </>
                }
                {createTemplateContent}
              </tbody>
          </reactbootstrap.Table>
          {this.state.showMovie &&
            <MovieDetails movieId={this.state.movieId}/>
          }
          {/* <div>
            <reactbootstrap.Button style={{ float: 'right',backgroundColor: '#EC661C',borderColor: "black", marginBottom: '25px', marginTop: '25px', width: '80px' }} type="button" onClick={e => this.logOut()}>
                  {'LogOut'}
            </reactbootstrap.Button>
          </div> */}
        </reactbootstrap.Form>
      </reactbootstrap.Container>
    )
  }

}

export default ManageMovies
