import React, { Component } from "react";
import * as reactbootstrap from "react-bootstrap";
import logo from '../as45.png';
import background from '../logo.svg'

class MovieDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movieData: {},
    };
  }

  componentDidMount() {
    let moviesList = localStorage.getItem('movies') ? JSON.parse(localStorage.getItem('movies')) : {}
    // let myMainSite = window.location.pathname
    var currentUrl = window.location.pathname.split('/');
    let movieId = currentUrl[2];
    let currentMovie = Object.values(moviesList).filter(movie => movie.id == movieId)
    if (Object.keys(currentMovie).length > 0) {
      let editMovie = currentMovie[0]
      this.setState({
        // name: editMovie.name,
        // year: editMovie.year,
        // rating: editMovie.rating,
        // duration: editMovie.duration,
        // image: editMovie.image,
        movieData: editMovie,
      });
    }
  }

  render () {
    const { movieData } = this.state
    let movieReleaseDate = movieData.year
    let date1 = new Date(movieReleaseDate);
    let dateOne = date1.getFullYear()
    console.log(dateOne);
    return (
      <reactbootstrap.Container className="pt-5 px-0">
        <div style={{ color: "#EC661C", fontSize: "20px" }}>
          <span>
            <h4>{"Movie details"}</h4>
          </span>
        </div>
        <reactbootstrap.Form>
          <div style={{ 'width': '100%', 'backgroundImage' : 'url(' + background + ')' , 'backgroundSize': "cover" }}>
            {/* <div style={{'width': '70%','display':'block' }}> */}
              <img src={background} style ={{ 'display':'block' , 'width':'100%', 'height':'100%', 'backgroundSize': "cover", 'objectFit': 'cover'}} className="App-logo" alt="movie" />
            {/* </div> */}
            <div style={{'width': '60%','float': 'right' }}>
              <div style={{'width': '30%','float': 'left' }}>
                <img src={logo} className="App-logo" alt="logo" />
              </div>
              <div style={{'width': '30%','float': 'left' }}>
                <h2>{movieData.name}</h2>
                <p>{"This is" + movieData.name + "movie description"}</p>
                <div>
                  <p style={{'width': '15%','float': 'left' }}>
                    {"Original release \n"}
                    {dateOne}
                  </p>
                  <p style={{'width': '15%','float': 'right' }}>
                    {"Rating \n"}
                    {movieData.rating}
                  </p>
                </div>
                <div></div>
              </div>
            </div>
          </div>
        </reactbootstrap.Form>
      </reactbootstrap.Container>
    )
  }
}

export default MovieDetails
